INSERT INTO `comments` (`id`, `details`, `user_id`, `news_id`, `flag`, `ip_address`, `created_at`, `updated_at`) VALUES
(1, '', 0, 1498975034, '2', '', '2017-07-18 18:00:00', NULL),
(2, '', 0, 1498975034, '2', '', '2017-07-05 00:00:00', NULL),
(3, '', 0, 1498975034, '2', '', '2017-06-16 00:00:00', NULL),
(4, '', 0, 1500100339, '2', '', '2017-07-22 11:19:26', NULL),
(5, ' তদন্তে ফরহাদ মজহারের ভিডিও, সিসিটিভি ফুটেজ এবং কল লিস্ট পাওয়া গেছে। দু-এক দিনের মধ্যেই সব তথ্য জাতিকে জানানো হবে।’', 0, 1499921559, '1', '', '2017-07-25 07:12:33', NULL),
(6, 'গতকাল বুধবার পুলিশের মহাপরিদর্শক এ কে এম শহীদুল হক প্রথম আলোকে বলেন, ‘ফরহাদ মজহার অপহৃত হননি। তিনি স্বেচ্ছায় ঢাকা ছেড়েছিলেন।’', 1500716783, 1499921559, '2', '', '2017-07-25 07:13:55', NULL),
(7, 'এতে মনে হচ্ছে, এখানে যে একটি অপহরণের ঘটনা আছে, অপহরণ হয়েছে তা আর দেখা হচ্ছে না। ওইখানেও তো ধরতে পারত, তাহলে তো বের হতো এটা অপহরণ কি না?’', 1500716783, 1499921559, '2', '', '2017-07-25 07:14:22', NULL),
(8, 'আমি তাঁর কাছ থেকে শুনে বলতে পারি। তবে তা ঠিক হবে বলে মনে করি না। তিনি দু-এক দিনের মধ্যেই বিবৃতি দেবেন। কী ঘটেছে সেটা তাঁর মুখ থেকে শোনাই ভালো হবে।’', 1500716783, 1499921559, '2', '', '2017-07-25 07:14:35', NULL);
